    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        div.setText(jComboBox1.getSelectedItem().toString());
        dist.setText(jComboBox2.getSelectedItem().toString());
        bg.setText(jComboBox3.getSelectedItem().toString());

        JOptionPane.showMessageDialog(rootPane, "Division: "+jComboBox1.getSelectedItem().toString()+"\n District: "
            +jComboBox2.getSelectedItem().toString()+"\n Blood Group: "
            +jComboBox3.getSelectedItem().toString());

        String Divition = div.getText();
        String Dist = dist.getText();
        String Bg = bg.getText();

        if(Divition.equals("RAJSHAHI") && Dist.equals("Joypurhat District")&&Bg.equals("O+")){
           Blood_joypurhat obj = new Blood_joypurhat();
            obj.setVisible(true);
            dispose();
        }

        else if(Divition.equals("CHITTAGONG") && Dist.equals("Cumilla District")&&Bg.equals("O+")){
            Blood_Comilla obj = new Blood_Comilla();
            obj.setVisible(true);
            dispose();
        }

        else{
            JOptionPane.showMessageDialog(rootPane, "SORRY!  I NEED TO UPDATE");
        }

    }//GEN-LAST:event_jButton1ActionPerformed

